    public static final String load = " SELECT NVL(TO_CHAR(LOD.EFFECT_DATE,'DD-Mon-RRRR'),'-') AS EFFECT_DATE,  NVL(TO_CHAR(LOD.NEW_CONNECTED_LOAD),'-') AS NEW_CONNLOAD,NVL(TO_CHAR(LOD.OLD_CONNECTED_LOAD),'-') AS OLD_CONNLOAD, (SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID IN(LOD.CREATED_BY)) AS CHANGED_BY,NVL(TO_CHAR(LOD.CREATE_DATE,'DD-Mon-RRRR'),'-') AS CREATE_DATE FROM LOAD_CHANGES_T LOD   WHERE LOD.RECORD_STATUS='1' AND (NEW_CONNECTED_LOAD!=OLD_CONNECTED_LOAD) AND LOD.EFFECT_DATE BETWEEN ADD_MONTHS(SYSDATE,'#param1#') AND SYSDATE AND  LOD.CONSUMER_ID IN(SELECT C.CONSUMER_ID FROM CONSUMER_M C WHERE C.CONSUMER_ID ='#param2#'  AND C.RECORD_STATUS=1)  GROUP BY LOD.EFFECT_DATE,LOD.NEW_CONNECTED_LOAD,LOD.OLD_CONNECTED_LOAD,LOD.CREATED_BY,LOD.CREATE_DATE  ORDER BY  LOD.EFFECT_DATE DESC ";
    public static final String phase = "select  NVL(TO_CHAR(PHASE.EFFECT_DATE,'DD-Mon-RRRR'),'-') AS EFFECT_DATE,PHASE.NEW_PHASE AS NEW_PHASE,PHASE.OLD_PHASE AS OLD_PHASE,(SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID IN(PHASE.CREATED_BY)) AS CHANGED_BY,NVL(TO_CHAR(PHASE.CREATE_DATE,'DD-Mon-RRRR'),'-') AS CREATE_DATE  from PHASE_CHANGES_T PHASE where PHASE.RECORD_STATUS='1' AND (NEW_PHASE!=OLD_PHASE) AND  PHASE.EFFECT_DATE BETWEEN ADD_MONTHS(SYSDATE,'#param2#') AND SYSDATE AND  PHASE.CONSUMER_ID IN(SELECT C.CONSUMER_ID FROM CONSUMER_M C WHERE C.CONSUMER_ID ='#param1#'  AND C.RECORD_STATUS=1)  ORDER BY PHASE.EFFECT_DATE desc";
    public static final String meter1 = "SELECT NVL(TO_CHAR(M.CHANGE_DATE,'DD-Mon-RRRR'),'-') AS CHANGE_DATE, (SELECT METER_NO FROM METER_STOCK_M WHERE METER_STOCK_ID = M.OLD_METER_NO) OLD_MTRNO,(SELECT METER_NO FROM METER_STOCK_M WHERE METER_STOCK_ID = M.NEW_METER_NO) NEW_MTRNO,(SELECT MAKE FROM METER_MAKE_M WHERE METER_MAKE_ID = M.OLD_METER_MAKE_ID) OLD_MTRMAKE ,(SELECT MAKE FROM METER_MAKE_M WHERE METER_MAKE_ID = M.NEW_METER_MAKE_ID) NEW_MTRMAKE, NVL((SELECT CAPACITY FROM METER_CAPACITOR_M WHERE METER_CAPACITOR_ID = M.OLD_METER_CAP),'-') OLD_MTRCAP,NVL((SELECT CAPACITY FROM METER_CAPACITOR_M WHERE METER_CAPACITOR_ID = M.NEW_METER_CAP),'-') NEW_MTRCAP, M.OLD_METER_KWH_RDG AS OLD_MTRRDG , M.NEW_METER_KWH_RDG AS NEW_MTRRDG,NVL((SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=NVL(M.APPROVED_BY,M.APPROVED_BY)),'-') AS APPROVE_BY,M.OLD_MF,M.NEW_MF,NVL(M.OLD_UNITS,0) OLDUNITS,(CASE WHEN T.CONSUMPTION_UNIT = 'KWH' THEN  CAST(NVL(AVG_UNITS,0) AS DECIMAL(15)) ELSE 0 END) AVG_UNITS,NVL(M.ASSESSED_UNITS,0) ASSESSED_UNITS, NVL(M.TOTAL_ASSD_UNITS,0) TOT_ASSD_UNITS,(SELECT METER_STATUS FROM METER_STATUS_M WHERE RECORD_STATUS=1 AND METER_STATUS_ID=M.OLD_STATUS) AS OLD_STATUS,NVL(REMARKS,'-'),NVL(TO_CHAR(M.CREATE_DATE,'DD-Mon-RRRR'),'-') AS ENTRY_DATE,NVL((TO_CHAR(M.APPROVED_DATE,'DD-Mon-RRRR')),'-')  AS APPROVE_DATE,M.OLD_METER_KVAH_RDG AS OLD_MTRRDGKvah ,M.NEW_METER_KVAH_RDG,M.ASSESSED_UNITS_KVAH,M.TOTAL_ASSD_UNITS_KVAH,NVL(M.OLD_UNITS_KVAH,0) OLDUNITSKVAH,NVL(TO_CHAR(M.NEW_SEALING_DATE,'DD-Mon-RRRR'),'-') AS NEW_SEALINGDATE, (SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=NVL(M.created_BY,'-')) AS CREATED_BY, NVL((SELECT NVL(METER_CHANGE_REASON,'-') FROM METER_CHANGES_REASON_M MCR WHERE MCR.RECORD_STATUS=1 AND MCR.METER_CHANGES_REASON_ID=M.ADDL_REMARKS),'-') AS REMARKS, (CASE WHEN T.CONSUMPTION_UNIT = 'KVAH' THEN  CAST(NVL(AVG_UNITS_KVAH,0) AS DECIMAL(15)) ELSE 0 END) AS AVG_UNITS_kvah,(CASE WHEN M.APPROVAL_STATUS IN('N','P') THEN 'PENDING' WHEN M.APPROVAL_STATUS IN('F') THEN 'FORWARDED'  WHEN M.APPROVAL_STATUS IN('A') THEN 'APPROVED' WHEN M.APPROVAL_STATUS IN('R') THEN 'REJECTED' ELSE '-' END) AS MC_STATUS   FROM CONSUMER_M C,METER_CHANGES_T M,TARIFF_PARA_T T WHERE C.CONSUMER_ID = M.CONSUMER_ID   AND NVL(TOD_ID,(SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE  RECORD_STATUS=1 AND PERIOD_TYPE IN( select PROCESS_BEHAV_LINK_ID from PROCESS_BEHAV_LINK_M where record_status=1 and BEHAVIOUR_ID in( select BEHAVIOUR_CNFG_ID from BEHAVIOUR_CNFG_M where RECORD_STATUS=1 AND BEHAVIOUR_CODE='TOD'))))  = (SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE  RECORD_STATUS=1 AND PERIOD_TYPE IN( select PROCESS_BEHAV_LINK_ID from PROCESS_BEHAV_LINK_M where record_status=1 and BEHAVIOUR_ID in( select BEHAVIOUR_CNFG_ID from BEHAVIOUR_CNFG_M where RECORD_STATUS=1 AND BEHAVIOUR_CODE='TOD')))  AND C.CONSUMER_ID = '#param1#' AND C.CATEGORY_ID=T.CATEGORY_ID AND T.RECORD_STATUS=1 AND C.RECORD_STATUS=1 AND M.RECORD_STATUS=1 AND TRUNC(M.CREATE_DATE) BETWEEN ADD_MONTHS(TRUNC(SYSDATE),'#param2#') AND TRUNC(SYSDATE) GROUP BY M.APPROVAL_STATUS,M.CHANGE_DATE,M.OLD_METER_NO,M.NEW_METER_NO,M.OLD_METER_MAKE_ID,M.NEW_METER_MAKE_ID,M.OLD_METER_CAP,M.NEW_METER_CAP,M.OLD_METER_KWH_RDG,M.OLD_METER_KVAH_RDG,M.NEW_METER_KWH_RDG,M.NEW_METER_KVAH_RDG, M.UPDATED_BY,M.OLD_MF,M.NEW_MF,M.OLD_UNITS,M.OLD_UNITS_KVAH,M.AVG_UNITS,M.ASSESSED_UNITS,M.ASSESSED_UNITS_KVAH,M.TOTAL_ASSD_UNITS,M.TOTAL_ASSD_UNITS_KVAH,M.OLD_STATUS,ADDL_REMARKS,M.CREATE_DATE,M.UPDATE_DATE, M.APPROVED_DATE,M.APPROVED_BY,M.CREATED_BY,T.CONSUMPTION_UNIT,M.AVG_UNITS_KVAH,M.NEW_SEALING_DATE ,m.approved_by,M.REMARKS ORDER BY trunc(NVL(M.CHANGE_DATE,'01-JAN-2000')) DESC ,trunc(NVL(M.CREATE_DATE,'01-JAN-2000')) DESC";
    public static final String meter2 = "SELECT NVL(TO_CHAR(M.CHANGE_DATE,'DD-Mon-RRRR'),'-') AS CHANGE_DATE,(SELECT METER_NO FROM METER_STOCK_M WHERE METER_STOCK_ID = M.OLD_METER_NO) OLD_MTRNO,(SELECT METER_NO FROM METER_STOCK_M WHERE METER_STOCK_ID = M.NEW_METER_NO) NEW_MTRNO, (SELECT MAKE FROM METER_MAKE_M WHERE METER_MAKE_ID = M.OLD_METER_MAKE_ID) OLD_MTRMAKE ,    (SELECT MAKE FROM METER_MAKE_M WHERE METER_MAKE_ID = M.NEW_METER_MAKE_ID) NEW_MTRMAKE, NVL((SELECT CAPACITY FROM METER_CAPACITOR_M WHERE METER_CAPACITOR_ID = M.OLD_METER_CAP),'-') OLD_MTRCAP, NVL((SELECT CAPACITY FROM METER_CAPACITOR_M WHERE METER_CAPACITOR_ID = M.NEW_METER_CAP),'-') NEW_MTRCAP, M.OLD_METER_KWH_RDG AS OLDMTRKWH,M.OLD_METER_KVA_RDG AS OLDMTRKVA,M.OLD_METER_KVAH_RDG AS OLDMTRKVAH, M.NEW_METER_KWH_RDG AS NEWMTRKWH, M.NEW_METER_KVA_RDG AS NEWMTRKVA,M.NEW_METER_KVAH_RDG AS NEWMTRKVAH, NVL((SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=NVL(M.APPROVED_BY,M.APPROVED_BY)),'-') AS APPROVE_BY,M.OLD_MF,M.NEW_MF,NVL(M.OLD_UNITS,0) OLDUNITS,NVL(M.AVG_UNITS,0) AVERAGE_UNITS,NVL(M.ASSESSED_UNITS,0) ASSESSED_UNITS,NVL(M.TOTAL_ASSD_UNITS,0) TOT_ASSD_UNITS, (SELECT CODE FROM METER_STATUS_M WHERE RECORD_STATUS=1 AND METER_STATUS_ID=M.OLD_STATUS) AS OLD_STATUS,NVL(ADDL_REMARKS,'-'),NVL(TO_CHAR(M.CREATE_DATE,'DD-Mon-RRRR'),'-') AS ENTRY_DATE,NVL((TO_CHAR(M.APPROVED_DATE,'DD-Mon-RRRR')),'-')  AS APPROVE_DATE, (SELECT PERIOD_NAME FROM TOD_PERIOD_CNFG_M WHERE  RECORD_STATUS=1 AND TOD_PERIOD_CNFG_ID=M.TOD_ID AND EFFECTIVE_DATE=(SELECT MAX(EFFECTIVE_DATE) FROM TOD_PERIOD_CNFG_M  WHERE RECORD_STATUS=1)) AS SLOTNAME,NVL(TO_CHAR(M.NEW_SEALING_DATE,'DD-Mon-RRRR'),'-') AS NEW_SEALINGDATE, (SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=NVL(M.created_BY,'-')) AS CREATED_BY, NVL((SELECT NVL(METER_CHANGE_REASON,'-') FROM METER_CHANGES_REASON_M MCR WHERE MCR.RECORD_STATUS=1 AND MCR.METER_CHANGES_REASON_ID=M.ADDL_REMARKS),'-') AS REMARKS, (CASE WHEN T.CONSUMPTION_UNIT = 'KVAH' THEN  CAST(NVL(AVG_UNITS_KVAH,0) AS DECIMAL(15)) ELSE 0 END) AS AVG_UNITS_kvah,(CASE WHEN M.APPROVAL_STATUS IN('N','P') THEN 'PENDING' WHEN M.APPROVAL_STATUS IN('F') THEN 'FORWARDED'  WHEN M.APPROVAL_STATUS IN('A') THEN 'APPROVED' WHEN M.APPROVAL_STATUS IN('R') THEN 'REJECTED' ELSE '-' END) AS MC_STATUS, NVL(CAST(M.OLD_CUML_RDG_KWH AS DECIMAL(15,2)),0) AS OLD_CUML_RDG_KWH,NVL(CAST(M.CUML_FINAL_KWH_RDG AS DECIMAL(15,2)),0) AS CUML_FINAL_KWH_RDG,NVL(CAST(M.CUML_AVG_KWH AS DECIMAL(15,2)),0) AS CUML_AVG_KWH ,NVL(CAST(M.CUML_OLDUNITS_KWH AS DECIMAL(15,2)),0) AS CUML_OLDUNITS_KWH,NVL(CAST(M.OLD_CUML_RDG_KVAH AS DECIMAL(15,2)),0) AS OLD_CUML_RDG_KVAH,NVL(CAST(M.CUML_FINAL_KVAH_RDG AS DECIMAL(15,2)),0) AS CUML_FINAL_KVAH_RDG,NVL(CAST(M.CUML_AVG_KVAH AS DECIMAL(15,2)),0) AS CUML_AVG_KVAH ,NVL(CAST(M.CUML_OLDUNITS_KVAH AS DECIMAL(15,2)),0) AS CUML_OLDUNITS_KVAH,NVL(CAST(M.OLD_CUML_RDG_KVA AS DECIMAL(15,2)),0) AS OLD_CUML_RDG_KVA,NVL(CAST(M.CUML_FINAL_KVA_RDG AS DECIMAL(15,2)),0) AS CUML_FINAL_KVA_RDG,NVL(CAST(M.CUML_AVG_KVA AS DECIMAL(15,2)),0) AS CUML_AVG_KVA ,NVL(CAST(M.CUML_OLDUNITS_KVA AS DECIMAL(15,2)),0) AS CUML_OLDUNITS_KVA   FROM CONSUMER_M C,METER_CHANGES_T M,TARIFF_PARA_T T  WHERE C.CONSUMER_ID = M.CONSUMER_ID  AND  C.CONSUMER_ID = '#param1#' AND C.CATEGORY_ID=T.CATEGORY_ID AND T.RECORD_STATUS=1 AND C.RECORD_STATUS=1 AND M.RECORD_STATUS=1 AND TRUNC(M.CREATE_DATE) BETWEEN ADD_MONTHS(TRUNC(SYSDATE),'#param2#') AND TRUNC(SYSDATE) GROUP BY M.CHANGE_DATE,M.OLD_METER_NO,M.NEW_METER_NO,M.OLD_METER_MAKE_ID,M.NEW_METER_MAKE_ID,M.OLD_METER_CAP,M.NEW_METER_CAP,M.OLD_METER_KWH_RDG,M.OLD_METER_KVA_RDG,M.OLD_METER_KVAH_RDG,M.NEW_METER_KWH_RDG,M.NEW_METER_KVA_RDG,M.NEW_METER_KVAH_RDG,M.UPDATED_BY,M.OLD_MF,M.NEW_MF,M.OLD_UNITS,M.AVG_UNITS,M.ASSESSED_UNITS,M.TOTAL_ASSD_UNITS,M.OLD_STATUS,ADDL_REMARKS,M.CREATE_DATE,M.UPDATE_DATE,M.APPROVED_DATE,M.APPROVAL_STATUS,T.CONSUMPTION_UNIT,M.AVG_UNITS,M.AVG_UNITS_KVAH,M.APPROVED_BY,M.CREATED_BY,M.TOD_ID ,M.NEW_SEALING_DATE,M.OLD_CUML_RDG_KWH,M.CUML_FINAL_KWH_RDG,M.CUML_AVG_KWH,M.CUML_OLDUNITS_KWH,M.OLD_CUML_RDG_KVAH,M.CUML_FINAL_KVAH_RDG,M.CUML_AVG_KVAH,M.CUML_OLDUNITS_KVAH,M.OLD_CUML_RDG_KVA,M.CUML_FINAL_KVA_RDG,M.CUML_AVG_KVA,M.CUML_OLDUNITS_KVA ORDER BY trunc(NVL(M.CHANGE_DATE,'01-JAN-2000')) DESC , trunc(NVL(M.CREATE_DATE,'01-JAN-2000')) DESC";
    public static final String checkmeter = " SELECT TO_CHAR(CH.CHK_INITIAL_RDG_DATE,'DD-MM-RRRR') AS INSTALLED_ON,  NVL((SELECT METER_NO FROM METER_STOCK_M WHERE RECORD_STATUS=1 AND METER_STOCK_ID=CH.CHK_METER_ID),'-')  CHK_MTR_NUMBER, NVL(CH.ACTUAL_METER_ID,'-') AS ACTUAL_METER_NUMBER, CH.CHK_UNITS AS CHECK_METER_CONSUMPTION,CH.ACTUAL_UNITS AS ACTAUL_METER_CONSUMPTION, TO_CHAR(CH.CHK_PRST_RDG_DATE,'DD-MM-RRRR') AS FINALIZATION_DATE, CH.DEVIATION_IN_PCT AS DEVIATION, DECODE(CH.CHECK_METER_INSTALL,'Y','INSTALLED','C','FINALIZED','D','DIRECT CHECK METER FINALIZED'), NVL((SELECT FIRST_NAME||' '||LAST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=CH.APPROVED_BY),(SELECT FIRST_NAME ||' '|| LAST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=CH.CREATED_BY)) AS CHANGED_BY,CAST(NVL(CH.ENERGY_CHG_ADJ,0) AS DECIMAL(15,2)) AS ECADJUSTED,CAST(NVL(CH.ED_CHG_ADJ,0) AS DECIMAL(15,2)) EDADJUSTED,NVL(CAST(CAST(CH.FINAL_ENERGY_CHG_ADJ AS DECIMAL(15,2)) AS VARCHAR2(15)) ,'-'),NVL(CAST(CAST(CH.FINAL_ED_CHG_ADJ AS DECIMAL(15,2)) AS VARCHAR2(15)) ,'-') FROM CHECK_METER_T CH,CONSUMER_M CM WHERE CH.RECORD_STATUS=1 AND CM.RECORD_STATUS=1 AND CM.CONSUMER_ID='#param1#' AND CM.CONSUMER_ID=CH.CONSUMER_ID AND NVL(CH.CHECK_METER_INSTALL,'X') IN ('Y','C','D') AND CH.CREATE_DATE BETWEEN ADD_MONTHS(SYSDATE,'#param2#') AND SYSDATE ORDER BY CH.CHK_INITIAL_RDG_DATE DESC";
