    public static final String METER_CONVERSIONS_QRY = "SELECT * FROM(  SELECT (SELECT PERIOD_NAME FROM TOD_PERIOD_CNFG_M TD WHERE TD.RECORD_STATUS=1 AND TD.TOD_PERIOD_CNFG_ID=CT.TOD_FLAG) AS SLOT,(SELECT CAT.NAME FROM CATEGORY_M CAT WHERE CATEGORY_ID=CT.OLD_CATEGORY_ID) AS OLD_CAT,NVL((SELECT CAT.NAME FROM CATEGORY_M CAT WHERE CATEGORY_ID=CT.NEW_CATEGORY_ID),'NO CHANGE') AS NEW_CAT,KWH_RDG AS KWH_RDG,KVAH_RDG AS KVAH_RDG,KVA_RDG AS KVA_RDG,TO_CHAR(EFFECTIVE_DATE,'DD-Mon-RRRR') AS EFFECT_DATE,CHANGE_TYPE AS CHANGE_TYPE,TOD_FLAG AS TOD_ID,CONSUMER_ID AS CONSUMER_ID  FROM CONS_METER_TYPE_CHANGES_T CT WHERE RECORD_STATUS=1 AND CONSUMER_ID='#param1#'  UNION ALL  SELECT '24 Hours' AS SLOT,(SELECT CAT.NAME FROM CATEGORY_M CAT WHERE CATEGORY_ID=CT.OLD_CATEGORY_ID) AS OLD_CAT,NVL((SELECT CAT.NAME FROM CATEGORY_M CAT WHERE CATEGORY_ID=CT.NEW_CATEGORY_ID),'NO CHANGE') AS NEW_CAT,CUML_KWH_RDG AS KWH_RDG,CUML_KVAH_RDG AS KVAH_RDG,CUML_KVA_RDG AS KVA_RDG,TO_CHAR(EFFECTIVE_DATE,'DD-Mon-RRRR')  AS EFFECT_DATE,CHANGE_TYPE AS CHANGE_TYPE,'100-3' AS TOD_ID,CONSUMER_ID AS CONSUMER_ID  FROM CONS_METER_TYPE_CHANGES_T CT WHERE RECORD_STATUS=1 AND TOD_FLAG='100-1' AND CONSUMER_ID='#param1#'  )  ORDER BY EFFECT_DATE DESC,TOD_ID asc ";
