    public static final String getMaxJEBucketData = " SELECT '-' AS DOC_NO, CODE, CAST( SUM(ECBAL) AS DECIMAL(20,2)) AS BALEC, CAST( SUM(EDBAL) AS DECIMAL(20,2)) AS BALED,  CAST( SUM(OTHBAL) AS DECIMAL(20,2)) AS BALOTH, CAST( SUM(DPSBAL) AS DECIMAL(20,2)) AS BALDPS, CAST( SUM(TCSBAL)  AS DECIMAL(20,2)) AS BALTCS, JE_PART_TYPE_ID  FROM (SELECT CASE WHEN JPTM.CODE= 'B4-2' THEN 'B4_2' ELSE JPTM.CODE END AS CODE, SGT.ACC_CODE,  (CASE WHEN SGT.ACC_CODE NOT IN  ('ED','DPS','OTH','TCSAMT') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) ECBAL ,  (CASE WHEN SGT.ACC_CODE = ('ED') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) EDBAL ,  (CASE WHEN SGT.ACC_CODE = ('DPS') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) DPSBAL ,  (CASE WHEN SGT.ACC_CODE = ('OTH') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) OTHBAL ,  (CASE WHEN SGT.ACC_CODE = ('TCSAMT') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) TCSBAL, JPTM.JE_PART_TYPE_ID AS JE_PART_TYPE_ID  FROM MBC_USER.JOURNAL_BOOK_T JB  INNER JOIN MBC_USER.SUBSIDIARY_GRP_T SGT  ON (JB.RECORD_STATUS = 1 AND SGT.RECORD_STATUS = 1 AND JB.SUBSIDIARY_ACNT_ID = SGT.SUBSIDIARY_GRP_ID AND CONSUMER_ID='#param1#' )  INNER JOIN MBC_USER.JE_PART_TYPE_M JPTM ON (JPTM.JE_PART_TYPE_ID = JB.JE_PART_TYPE_ID AND JPTM.OPERATIONAL_FLAG = 0 AND JPTM.CODE!='B5')  GROUP BY JPTM.CODE,SGT.ACC_CODE,JPTM.JE_PART_TYPE_ID) GROUP BY CODE,JE_PART_TYPE_ID ";
    public static final String getJEBucketDetail = " SELECT '-' AS DOC_NO, CODE, TRIM(TO_CHAR(SUM(ECBAL),'99,99,99,99,99,990.00')) AS BALEC, TRIM(TO_CHAR(SUM(EDBAL),'99,99,99,99,99,990.00')) AS BALED,  TRIM(TO_CHAR(SUM(OTHBAL),'99,99,99,99,99,990.00')) AS BALOTH, TRIM(TO_CHAR(SUM(DPSBAL),'99,99,99,99,99,990.00')) AS BALDPS, TRIM(TO_CHAR(SUM(TCSBAL),'99,99,99,99,99,990.00')) AS BALTCS,  JE_PART_TYPE_ID, TRIM(TO_CHAR(NVL(BALEC1,0),'99,99,99,99,99,990.00')) AS BALEC1, TRIM(TO_CHAR(NVL(BALED1,0),'99,99,99,99,99,990.00')) AS BALED1, TRIM(TO_CHAR(NVL(BALOTH1,0),'99,99,99,99,99,990.00')) AS BALOTH1, TRIM(TO_CHAR(NVL(BALDPS1,0),'99,99,99,99,99,990.00')) AS BALDPS1, TRANS_DATE, TRIM(TO_CHAR(NVL(BUCKETADJ_AMT,0),'99,99,99,99,99,990.00')) AS BUCKETADJ_AMT  FROM (SELECT JPTM.CODE, SGT.ACC_CODE,  (CASE WHEN SGT.ACC_CODE NOT IN  ('ED','DPS','OTH','TCSAMT') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) ECBAL ,  (CASE WHEN SGT.ACC_CODE = ('ED') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) EDBAL ,  (CASE WHEN SGT.ACC_CODE = ('DPS') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) DPSBAL ,  (CASE WHEN SGT.ACC_CODE = ('OTH') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) OTHBAL ,  (CASE WHEN SGT.ACC_CODE = ('TCSAMT') THEN SUM(DR_AMOUNT)-SUM(CR_AMOUNT) ELSE 0  END) TCSBAL, JPTM.JE_PART_TYPE_ID AS JE_PART_TYPE_ID,  BET.PREV_FINYR_ARR AS BALEC1, BET.PREV_FINYR_ED AS BALED1, 0 AS BALOTH1, BET.PREV_FINYR_SUR_CHG AS BALDPS1,TO_CHAR(BET.ADJUSTMENT_DATE, 'DD-MM-YYYY') AS TRANS_DATE, NVL((BET.PREV_FINYR_ARR + BET.PREV_FINYR_ED + BET.PREV_FINYR_SUR_CHG),0) AS BUCKETADJ_AMT   FROM MBC_USER.JOURNAL_BOOK_T JB  INNER JOIN MBC_USER.SUBSIDIARY_GRP_T SGT  ON (JB.RECORD_STATUS = 1 AND SGT.RECORD_STATUS = 1 AND JB.SUBSIDIARY_ACNT_ID = SGT.SUBSIDIARY_GRP_ID AND CONSUMER_ID='#param1#' )  INNER JOIN MBC_USER.JE_PART_TYPE_M JPTM ON (JPTM.JE_PART_TYPE_ID = JB.JE_PART_TYPE_ID AND JPTM.OPERATIONAL_FLAG = 0 AND JPTM.CODE!='B5')  INNER JOIN MBC_USER.ADJUSTMENTS_T BET  ON (BET.RECORD_STATUS=1 AND BET.JE_PART_TYPE_ID=JPTM.JE_PART_TYPE_ID AND BET.CONSUMER_ID='#param1#' AND BET.REVISED_BILL_DETAILS_ID='#param2#' AND BET.REVISED_FLAG='F' AND BET.ADJ_TYPE='JE' )  GROUP BY JPTM.CODE,SGT.ACC_CODE,JPTM.JE_PART_TYPE_ID, BET.PREV_FINYR_ARR , BET.PREV_FINYR_ED  ,BET.PREV_FINYR_SUR_CHG, BET.ADJUSTMENT_DATE  )  GROUP BY CODE,JE_PART_TYPE_ID,BALEC1,BALED1,BALOTH1,BALDPS1,TRANS_DATE,BUCKETADJ_AMT ORDER BY CODE ASC";
    public static final String getJEAmtAdjApprove = " SELECT A.CONSUMER_ID ID, A.SCNO, B.REVISED_MONTH, B.REVISED_YEAR, 0 AS UNITS,  NVL(B.TOTAL_ADJ_AMOUNT,0) AS ADJAMT, DECODE(B.REVISION_CODE, 'A', 'DEBIT', 'S', 'CREDIT') AS REVISION_CODE, B.ADJUSTMENTS_ID AS CCBR_ID, NVL(A.CONTRACTED_LOAD,0) AS LOAD, A.LOAD_UNIT,  (SELECT CAT_CODE FROM CATEGORY_M WHERE RECORD_STATUS=1 AND CATEGORY_ID=A.CATEGORY_ID) AS CAT,  (SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=B.CREATED_BY) AS REVISED_BY,  NVL((SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=B.UPDATED_BY),  (SELECT FIRST_NAME FROM EMPLOYEE_M WHERE RECORD_STATUS=1 AND USER_ID=B.CREATED_BY)) AS FORWARDED_BY, B.REVISED_BILL_DETAILS_ID REVISED_BILL_ID,  (SELECT SHORT_NAME FROM DESIGNATION_M WHERE DESIGNATION_ID IN(SELECT DESIGNATION_ID FROM DESIGNATION_M WHERE DESIGNATION_ID=B.FORWARDED_TO)) AS PENDING_AT,  NVL(WAIVER_OF_SUR_CHG,0) AS WAIVER_OF_SURCHG, A.BILLING_STATUS  FROM CONSUMER_M A, ADJUSTMENTS_T B, OFFICE_M OM  WHERE A.CONSUMER_ID=B.CONSUMER_ID AND OM.OFFICE_ID=A.OFFICE_ID   AND B.REVISED_FLAG='F' AND A.RECORD_STATUS=1 AND B.ADJ_TYPE='JE' AND B.RECORD_STATUS=1 AND TO_CHAR(B.CREATE_DATE,'MMYY')=#param1##param2#  AND B.FLAG1 IS NOT NULL";
