    public static final String fetchconsumption = " SELECT CONSUMER_ID, RDG_MONTH, RDG_YEAR,BILL_DETAILS_ID,PRST_RDG_DATE,PREV_RDG_DATE, CUML_KWH_UNITS, CUML_KVAH_UNITS, MD_KVA, PEAK_KWH_UNITS, OFFPEAK_KWH_UNITS  FROM (SELECT C.CONSUMER_ID, T.RDG_MONTH, T.RDG_YEAR, TO_DATE(T.RDG_YEAR||LPAD(T.RDG_MONTH,2,0),'RRRRMM') MNTHYR,B.BILL_DETAILS_ID,T.PRST_RDG_DATE,T.PREV_RDG_DATE,  (CASE WHEN C.LOAD_TYPE='T' THEN NVL(T.CUML_KWH_UNITS,0) ELSE NVL(T.BILLED_KWH,0) END) AS CUML_KWH_UNITS,  (CASE WHEN C.LOAD_TYPE='T' THEN NVL(T.CUML_KVAH_UNITS,0) ELSE NVL(T.BILLED_KVAH,0) END) AS CUML_KVAH_UNITS, (CASE WHEN C.LOAD_TYPE='T' THEN NVL(T.REC_MDKVA,0) ELSE NVL(T.BILLED_KVA,0) END) AS MD_KVA, (CASE WHEN C.LOAD_TYPE='T' THEN NVL(T.BILLED_KWH,0) ELSE 0 END) AS PEAK_KWH_UNITS, (CASE WHEN C.LOAD_TYPE='T' THEN NVL(OPR.BILLED_KWH,0) ELSE 0 END) AS OFFPEAK_KWH_UNITS  FROM COMMON_USER.CONSUMER_M C INNER JOIN METER_RDG_III_T T ON T.CONSUMER_ID=C.CONSUMER_ID AND T.RECORD_STATUS=1 AND NVL(T.CUML_RDG_FLAG, 1)=1  AND C.RECORD_STATUS=1 AND C.SCNO='#param1#'  INNER JOIN BILL_DETAILS_T B ON B.CONSUMER_ID=C.CONSUMER_ID AND B.METER_RDG_III_ID=T.METER_RDG_III_ID AND B.RECORD_STATUS=1  AND B.BILLMONTH=T.RDG_MONTH AND B.BILLYEAR=T.RDG_YEAR  LEFT OUTER JOIN METER_RDG_III_T OPR ON  (OPR.CONSUMER_ID=C.CONSUMER_ID AND OPR.RECORD_STATUS=1 AND OPR.RDG_MONTH=T.RDG_MONTH AND OPR.RDG_YEAR=T.RDG_YEAR)  AND OPR.TOD_ID = (SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE RECORD_STATUS=1  AND PERIOD_TYPE = (SELECT PROCESS_BEHAV_LINK_ID FROM PROCESS_BEHAV_LINK_M WHERE RECORD_STATUS=1  AND BEHAVIOUR_ID= (SELECT BEHAVIOUR_CNFG_ID FROM BEHAVIOUR_CNFG_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_CODE='OFFPK')))  ORDER BY T.RDG_YEAR DESC , T.RDG_MONTH DESC FETCH FIRST 12 ROWS ONLY ) ";
    public static final String fetchcumlrdgs = " SELECT ROWNUM AS NUM, A.TOD_FLAG AS TOD_FLAG,NVL(A.BILLED_KWH,0) BILLED_KWH,NVL(A.BILLED_KVA,0) BILLED_KVA,NVL(A.BILLED_KVAH,0) BILLED_KVAH,  NVL(BL.BILLED_MONTHS,C.BILLING_CYCLE) BILLED_MONTHS, NVL(CUML_KWH_UNITS,0) CUML_KWH_UNITS, NVL(CUML_KVA_UNITS ,0.0) MDKVA, NVL(CUML_KVAH_UNITS,0) CUML_KVAH_UNITS,  DENSE_RANK() OVER (ORDER BY PRST_RDG_DATE DESC) RNK,M.BILL_BASIS ,NVL(CUML_RDG_FLAG,0),NVL(POWER_FACTOR,0) ,  CAST(NVL((SELECT NVL(MRT.BILLED_KWH,0) FROM METER_RDG_III_T MRT WHERE MRT.RECORD_STATUS=1 AND MRT.CONSUMER_ID=A.CONSUMER_ID AND MRT.RDG_MONTH||'/'||MRT.RDG_YEAR=A.RDG_MONTH||'/'||A.RDG_YEAR and MRT.RDG_MONTH!=0 AND MRT.TOD_ID IN (SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE RECORD_STATUS=1 AND PERIOD_TYPE IN (SELECT PROCESS_BEHAV_LINK_ID FROM PROCESS_BEHAV_LINK_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_ID IN (SELECT BEHAVIOUR_CNFG_ID FROM BEHAVIOUR_CNFG_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_CODE='OFFPK')))),0) AS DECIMAL(15,2)) AS OFFPEAK_KWHUNITS,  (SELECT CAST((MT.BILLED_KWH) AS DECIMAL(15,2)) AS BILLEDKWH  FROM MULTI_METER_RDG_III_T MT INNER JOIN MULTI_METER_CONS_REL_M MM ON MT.CONSUMER_ID= MM.CONSUMER_ID  AND MM.RECORD_STATUS=1 AND MM.METER_STOCK_ID=MT.METER_STOCK_ID WHERE MT.RECORD_STATUS=1 AND MT.METER_USAGE_TYPE=MM.METER_USAGE_TYPE AND MT.RDG_MONTH=A.RDG_MONTH  AND MT.RDG_YEAR=A.RDG_YEAR AND MT.CONSUMER_ID IN (SELECT CONSUMER_ID FROM METER_RDG_III_T MI WHERE MI.RECORD_STATUS=1 AND MI.RDG_MONTH=MT.RDG_MONTH  AND MI.RDG_YEAR=MT.RDG_YEAR AND MT.CUML_RDG_FLAG=1 AND NVL(MI.CUML_RDG_FLAG, 1)=1 AND MI.CONSUMER_ID='#param1#') AND MT.METER_USAGE_TYPE='C') AS COLONY_KWH_UNITS,  (SELECT MM.CATEGORY_ID  AS CATID FROM MULTI_METER_RDG_III_T MT  INNER JOIN MULTI_METER_CONS_REL_M MM ON MT.CONSUMER_ID= MM.CONSUMER_ID  AND MM.RECORD_STATUS=1 AND MM.METER_STOCK_ID=MT.METER_STOCK_ID WHERE MT.RECORD_STATUS=1 AND MT.METER_USAGE_TYPE=MM.METER_USAGE_TYPE AND MT.RDG_MONTH=A.RDG_MONTH  AND MT.RDG_YEAR=A.RDG_YEAR AND MT.CONSUMER_ID IN (SELECT CONSUMER_ID FROM METER_RDG_III_T MI WHERE MI.RECORD_STATUS=1 AND MI.RDG_MONTH=MT.RDG_MONTH  AND MI.RDG_YEAR=MT.RDG_YEAR AND MT.CUML_RDG_FLAG=1 AND NVL(MI.CUML_RDG_FLAG, 1)=1 AND MI.CONSUMER_ID='#param1#') AND MT.METER_USAGE_TYPE='C') AS COLONY_CAT_ID,  NVL((SELECT NVL(MRAT.POWER_OFF_HOURS,0) FROM METER_RDG_ADDL_ATTRIBUTES_T MRAT WHERE A.TOD_ID=MRAT.TOD_ID AND MRAT.RECORD_STATUS=1 AND MRAT.CONSUMER_ID=A.CONSUMER_ID  AND MRAT.RDG_MONTH||'/'||MRAT.RDG_YEAR=A.RDG_MONTH||'/'||A.RDG_YEAR ),0) AS OUTAGE_HRS,  NVL(A.BILLED_KVA,0) AS BILLED_KVA1, NVL(A.MD_KVA,0) AS MD_KVA, NVL(A.REC_MDKVA,0) AS REC_MDKVA,  CAST(NVL((SELECT CASE WHEN NVL(MRT.CUML_RECOMM_KVA,0)>0 THEN NVL(MRT.REC_MDKVA,0) ELSE NVL(MRT.BILLED_KVA,0) END FROM METER_RDG_III_T MRT WHERE MRT.RECORD_STATUS=1 AND MRT.CONSUMER_ID=A.CONSUMER_ID AND MRT.RDG_MONTH||'/'||MRT.RDG_YEAR=A.RDG_MONTH||'/'||A.RDG_YEAR and MRT.RDG_MONTH!=0 AND MRT.TOD_ID IN (SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE RECORD_STATUS=1 AND PERIOD_TYPE IN (SELECT PROCESS_BEHAV_LINK_ID FROM PROCESS_BEHAV_LINK_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_ID IN (SELECT BEHAVIOUR_CNFG_ID FROM BEHAVIOUR_CNFG_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_CODE='TOD')))),0) AS DECIMAL(15,2)) AS PEAK_KVAUNITS,  CAST(NVL((SELECT NVL(MRT.BILLED_KVA,0) FROM METER_RDG_III_T MRT WHERE MRT.RECORD_STATUS=1 AND MRT.CONSUMER_ID=A.CONSUMER_ID AND MRT.RDG_MONTH||'/'||MRT.RDG_YEAR=A.RDG_MONTH||'/'||A.RDG_YEAR and MRT.RDG_MONTH!=0 AND MRT.TOD_ID IN (SELECT TOD_PERIOD_CNFG_ID FROM TOD_PERIOD_CNFG_M WHERE RECORD_STATUS=1 AND PERIOD_TYPE IN (SELECT PROCESS_BEHAV_LINK_ID FROM PROCESS_BEHAV_LINK_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_ID IN (SELECT BEHAVIOUR_CNFG_ID FROM BEHAVIOUR_CNFG_M WHERE RECORD_STATUS=1 AND BEHAVIOUR_CODE='OFFPK')))),0) AS DECIMAL(15,2)) AS OFFPEAK_KVAUNITS,  NVL(BL.BILL_BASIS,'0') AS BILL_BASIS1,A.TOD_ID  FROM METER_RDG_III_T A, METER_STATUS_M M, BILL_DETAILS_T BL,CONSUMER_M C  WHERE C.RECORD_STATUS=1 AND A.RECORD_STATUS = 1 AND C.CONSUMER_ID='#param1#'  AND C.CONSUMER_ID=A.CONSUMER_ID and A.rdg_month = Bl.billmonth and a.rdg_year= bl.billyear and (TRUNC(bl.BILL_ISSUE_DATE) between TO_DATE('#param2#','YYYY/MM/DD')  AND TO_DATE('#param3#','YYYY/MM/DD')) AND M.METER_STATUS_ID = A.PRST_STATUS_KWH AND M.METER_STATUS_ID = A.PRST_STATUS_KWH  AND BL.METER_RDG_III_ID(+) = A.METER_RDG_III_ID AND BL.RECORD_STATUS(+) = 1 AND A.RECORD_STATUS=1 AND NVL(A.RDG_USE,'Y')='Y'  ORDER BY PRST_RDG_DATE DESC,TOD_FLAG ";
